PROC ADAPTIVEREG <DATA=SAS-data-set> <DETAILS<=(detail-options)>> <NAMELEN=number> <NLOPTIONS(options)> <NOTHREADS> <OUTDESIGN<(options)>=SAS-data-set> <PLOTS=ALL | COMPONENTS | DIAGNOSTICS... > <SEED=number> <SINGULAR=number | EPSILON=number> <TESTDATA=SAS-data-set> <THREADS=n> <VALDATA=SAS-data-set>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <DESCENDING> <ORDER=<DATA | FORMATTED | FREQ>... > ; 

	FREQ variable ;     
	MODEL <ADDITIVE> <ALPHA=number> <CVMETHOD=<RANDOM(n) | INDEX(variable)>> ...; 

	OUTPUT <COOKD=> <COVRATIO=> <OUT=SAS-data-set> ...; 

	PARTITION <FRACTION(<fraction> <VALIDATE=fraction>)> <ROLEVAR=variable (<TEST='value'> <TRAIN='value'> <VALIDATE='value'>)> ; 

	SCORE <DATA=SAS data set> <OUT=SAS data set> <PREDICTED <=name>> ...; 

	WEIGHT variable ;
