PROC AUTOREG <COVOUT> <DATA= SAS-data-set> <OUTEST= SAS-data-set> <PLOTS=ALL | ACF | IACF... >;     
	BY variables ;

	CLASS variables ;

	HETERO <COEF=<NONNEG | UNIT | ZERO>... > <LINK=<EXP | SQUARE | LINEAR>> <STD=<NONNEG | UNIT | UNREST>> ...;

	LAGLIST;

	MODEL <CENTER> <NLAG= number | NLAG= (number-list)> <NOINT> ...;

	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r[n] | ABSFTOL=r[n> <ABSGCONV=r[n] | ABSGTOL=r[n> ...;

	OUTPUT <ALPHACLI= number> <ALPHACLM= number> <OUT= SAS-data-set> ...;

	RESTRICT equation , ..., equation ;

	TEST <TYPE=<F | WALD | LM>... > ;
