	ABORT <ABEND> <CANCEL> <CANCEL FILE> ...;     
	BOUNDS constraint <, constraint ...> ;     
	BY <DESCENDING> <NOTSORTED> ; 

	CALL routine(parameter-1<, ...parameter-n>); 

	COSAN <VAR=variable-list> ; 

	COV assignment <, assignment ...> ; 

	DELETE;     
	DETERM <LABEL | NAME = name> ;     
	DO <OVER> <> <> ...; 

	EFFPART effect < , effect> ; 

	ELSE <DO> <IF> <THEN> ...; 

	END;     
	FACTOR <COMPONENT> <GAMMA=p> <HEYWOOD> ...; 

	FITINDEX <ALPHAECV=α> <ALPHARMS=α> <CHICORRECT=<EQVARCOV | CIRCULARITY | EQCOVMAT>> ...; 

	FREQ variable;     
	GOTO label;     
	GROUP <DATA=SAS-data-set> <INWGT<(INV)>=SAS-data-set> <LABEL= | NAME=name> ...;     
	IF <DO> <THEN> <WHEN> ...; 

	LINCON constraint <, constraint ...> ; 

	LINEQS equation <, equation ...> ; 

	LINK statement-label;     
	LISMOD <var_lists> ;     
	LMTESTS <DEFAULT> <MAXRANK> <NODEFAULT> ...; 

	MATRIX matrix-name <location> = list <, location = list ...> ;     
	MEAN assignment <, assignment ...> ;     
	MODEL <CORRELATION> <GROUP= | GROUPS=int-list> <LABEL= | NAME=name> ...; 

	MSTRUCT <VAR=> ; 

	NLINCON | NLC constraint <, constraint ...> ;     
	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r <n> | ABSFTOL=r<n>> <ABSGCONV= | ABSGTOL=r <n>> ...;     
	; 

	OUTFILES | OUTFILE file_option <file_option ...> ;     
	PARAMETERS | PARMS parameter(s) << = > number(s) >;     
	PARTIAL variables ;     
	PATH path < , path ...> ; 

	PCOV assignment <, assignment ...> ; 

	PUT <_PAGE_> ;  

	PVAR assignment < , assignment ...> ; 

	RAM <VAR=variable-list | variable-list=number-list <,;     
	REFMODEL <ALLNEWPARMS> <PARM_PREFIX=prefix> <PARM_SUFFIX=suffix> ...; 

	RENAMEPARM assignment <, assignment ...> ; 

	RETURN;     
	; 

	SIMTESTS sim_test <sim_test ...> ; 

	STD assignment <, assignment ...> ; 

	STOP ;     
	STRUCTEQ variables </ label> ;     
	TESTFUNC functions ;     
	VAR;     
	VARIANCE assignment <, assignment ...> ;     
	VARNAMES | VNAMES assignment <, assignment ...> ;     
	WEIGHT variable;     
	WHEN <DO> ;
