PROC             CAS ;
	ACTION <action-set-name.action-name><RESULT=<variable>><STATUS=<rc>><ASYNC=name>
		 /           <parameters>;

	 target =           expression;

	CALL function            (argument1,           argument2...);

	CONTINUE ;

	DEFAULT RESULT=variable-expressionSTATUS=variable-expression           | CLEAR<clear-option-1<clear-option-2< ...clear-option-N>>>;

	DELETE value;

	DESCRIBE variable;

	DO ;
		... more CASL statements                 ...;
		END ;

	DO             UNTIL 
		condition; 
		... more CASL                 statements ...; 
		END ;

	DO WHILE condition;             
		... more CASL statements                 ...;
		END ;

	DO            [<key,>] <var>OVER <value>;             
		... more                 CASL statements ...;
		END ;

	DO <var>           =           <start>             [TO <stop>]             [BY <increment>]
		[WHILE <condition> |               UNTIL <condition>];
		... more CASL statements             ...;
		END ;

	ENDEXTERNALSOURCE ;

	ENDSOURCE ;

	EXIT expression;

	EXTERNALSOURCE variable-expression;
		<embedded-text>
		ENDEXTERNALSOURCE ;

	FILE fileref;

	FNC <category-name>;

	FORMAT variable-1...variable-Nformat;

	FUNCTION function name( [argument 1 [, argument 2...] ]             );             
		... more CASL statements             ...;
		END ;

	FUNCTIONLIST <name>;
		FLIST <name>;

	GLOBAL variable;

	GOTO label;

	IF condition
		THEN statement;
		ELSE              <statement>;

	Label statement

	LOCAL variable;

	LEAVE ;

	LOADACTIONSET actionsetname;

	NULL ;

	OUTPUT  <ODS | LOG>;

	PRINT value-1...value-N;

	PRINTLIST expression;

	RETURN <expression | value>;

	SAVERESULT variable-name<NOREPLACE >
		<DATAOUT =<libref.>               data-set-name> |           <LIB =libref>           |           
		<FILE =<pathname> | <filename>>
		<<CASLIB= casref> | <CASOUT= name>>;

	SELECT <(select-expression)>;
		WHEN -1 <(when-expression-1 ...,                   when-expression-n> )                statement;
			<...WHEN -n                 (when-expression-1 ..., when-expression-n) >statement;
		<OTHERWISE statement>;END ;

	SESSION session-reference;

	SET   (DISP<ECHOEXTSRC><LOGS><LOGS=DEBUG | NOTE | TRACE | WARN><STDJSON><WARNINGDUP><USEWIDTH>)           ;

	SOURCE variable;           text;           endsource;

	UNSET DISP<ECHOEXTSRC><LOGS><STDJSON><USEWIDTH ><WARNINGDUP>

	UPLOAD 
		PATH= path-to-file
		<CASOUT= {output-table-options>}
		<IMPORTOPTIONS= {FILETYPE=                  “BASESAS | ”                  “CSV” | “DTA” | “ESP” | “EXCEL” | “FMT” | “EXCEL” | “HDAT” | “JMP” | “LASR” | “SPSS” | “XLSX”,fileType-specific-parameters}>;

	RUN; 
QUIT ;

Form 1: 
DEFAULT RESULT=variable-expression;
Form 2: 
DEFAULT STATUS=variable-expression;
Form 3: 
DEFAULT CLEAR<clear-option-1<clear-option-2< ...clear-option-N>>>

FUNCTION function- name (<parameter–1><, parameter–2><, ..., parameter–n>); 
	statements;
END ;

Form 1: 
SAVERESULT variable-name<DATAOUT=<libref.>data-set-name | LIB=libref><REPLACE | NOREPLACE>;
Form 2: 
SAVERESULT variable-name<CSV="file-name" | FILE=output-file><REPLACE | NOREPLACE>;
Form 3: 
SAVERESULT variable-name<CASLIB=caslib-reference-name><CASOUT="table-name"><REPLACE | NOREPLACE>;

SELECT <(select-expression)>;
	WHEN -1 <(when-expression-1 ..., when-expression-n> )  statement;
		<...WHEN -n (when-expression-1 ..., when-expression-n) >statement;
	<OTHERWISE statement>;END ;
