PROC CAUSALMED <ALPHA=p> <CASECONTROL> <DATA=SAS-data-set> <DECOMP<=i>> <DESCENDING> <NLOPTIONS(options)> <NOPRINT> <PALL> <PMEDMOD> <POUTCOMEMOD> <PSHORT> <PSUMMARY> <RORDER=DATA | FORMATTED | FREQ... > <SINGULAR=tolerance> <THREADS=n | NTHREADS=n>;     
	BOOTSTRAP <BOOTCI <(BC|NORMAL|PERC|ALL)> | CI <(BC|NORMAL|PERC|ALL)>> <NBOOT=n | NSAMPLE=n | NSAMPLES=n> <SEED=n> ...; 

	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <CPREFIX=n> <DESCENDING> <LPREFIX=n> ...; 

	COVAR effects;     
	EVALUATE _CONTROL=FIRST|LAST|MAX...  _MEDIATORFIRST|LAST|MAX...  _TREATMENTFIRST|LAST|MAX...  ...;     
	FREQ variable ; 

	MEDIATOR mediator=treatment;     
	MODEL <DIST=<BIN | NEGBIN | NORMAL>... > <LINK=<IDENTITY | LOG | LOGIT>> ;
