PROC CCDM <ADJUSTEDSEVERITY=symbol-name | ADJSEV=symbol-name> <COUNTSTORE=SAS-item-store> <DATA=CAS-libref.data-table> <IGNOREPARMCOVARIANCE> <MAXCOUNTDRAW=number | MAXCOUNT=number> <NOPRINT> <NPERTURBEDSAMPLES=number | NPERTURB=number> <NREPLICATES=number | NREP=number> <OUTDRAW=CAS-libref.data-table | OUTSAMPLE=CAS-libref.data-table> <PCTLDEF=1 | 2 | 3... > <PRINT <(global-display-option)> =display-optionPRINT <(global-display-option)> =(display-option …display-option)> <SEED=number> <SEVERITYEST=CAS-libref.data-table> <SEVERITYSTORE=SAS-item-store | SEVSTORE=SAS-item-store> <VARDEF=divisor>;     
	ABORT <ABEND> <CANCEL> <CANCEL FILE> ...;     
	ARRAY <_ALL_> <_NUMERIC_> <_TEMPORARY_> ...; 

	ATTRIB <FORMAT=format> <INFORMAT=informat> <LABEL='label'> ...; 

	BY variable-list;     
	CALL routine(parameter-1<, ...parameter-n>);     
	CONTINUE; 

	DELETE;     
	DESCRIBE; 

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...; 

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...; 

	DO <OVER> <> <> ...; 

	;     
	; 

	DROP variable-list;     
	ELSE <DO> <IF> <THEN> ...; 

	END;     
	ENDRSUBMIT; 

	ERROR <message>;     
	EXECUTE; 

	EXTERNALCOUNTS <COUNT=count-variable> <ID=replication-id-variable> ; 

	FILE <DISK> <DUMMY> <LOG> ...; 

	FORMAT <DEFAULT=default-format> ; 

	GO TO label | GOTO label;     
	IF <DO> <THEN> <WHEN> ...; 

	INFILE <DATALINES4> <DATALINES> <DISK> ...; 

	INFORMAT <DEFAULT= default-informat> ; 

	INPUT <specification(s)><@|@@>; 

	KEEP variable-list;     
	; 

	LEAVE;     
	LENGTH <DEFAULT=n> ; 

	LINK label;     
	LIST; 

	LOCK <CLEAR> <LIST> ; 

	MERGE <ALTER=> <BUFNO=<n | nK | hexX>... > <END=variable> ...; 

	MISSING character(s);     
	MODIFY <END=variable> <KEY=index> <NOBS=variable> ...; 

	OTHERWISE <DO> ; 

	OUTPUT ADJSAMPLEVAR=variable-name | ADJSAMPLEVAR=(variable-name <…variable-name>) OUT=CAS-libref.data-table | OUTSAMPLE=CAS-libref.data-table SAMPLEVAR=variable-name ...;     
	OUTSUM <KURTOSIS|KURT < =variable-name >> <MEAN < =variable-name >> <OUT=CAS-libref.data-table | OUTSUM=CAS-libref.data-table> ...; 

	PAGE;     
	PUT <_ALL_> <_INFILE_> <_ODS_> ...; 

	PUTLOG 'message';     
	RENAME old-name-1=new-name-1 ... <old-name-n=new-name-n>; 

	REPLACE <data-set-name-1><...data-set-name-n>; 

	RETAIN <element-list(s) <initial-value(s) |; 

	RETURN;     
	RSUBMIT <options>; ENDRSUBMIT <CANCEL>;     
	; 

	SET <END=variable> <INDSNAME=variable> <KEY=index</UNIQUE>> ...; 

	SEVERITYMODEL <DEFINITIONSTABLE="CAS-table-name" <(CASLIB="caslib")> | SEVDEF="CAS-table-name" <(CASLIB="caslib")>> ;   

	SKIP <n>;     
	STOP; 

	WHEN <DO> ;     
	WHERE where-expression-1;
