PROC CNTSELECT <CORRB> <COVB> <COVEST=OP | HESSIAN | QML> <DATA=SAS-data-set> <GROUPID=variable> <MAXFUNC= | MAXFU=i> <MAXITER=i | MAXIT=i> <MAXTIME=r> <METHOD=CONGRA | DBLDOG | NONE... > <NOPRINT> <PRINTALL> <PRINTINTERNALNAMES> <PRINTTIMING> <>;     
	BOUNDS bound1 [, bound2 …]; 

	BY variables;     
	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...; 

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...; 

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...; 

	;     
	EFFECT <DETAILS> NAME=COLLECTION|MULTIMEMBER|MM|POLYNOMIAL|POLY...  <NOEFFECT> ...;     
	FREQ freq-variable;     
	INIT initialization1 < , initialization2 …>;     
	MODEL <DIST=<POISSON | CMPOISSON | NEGBIN(P=1)>... > <ERRORCOMP=<FIXED | RANDOM>> <NOINT> ...; 

	OUTPUT <COPYVAR=SAS-variable-names | COPYVARS=SAS-variable-names> <OUT=SAS-data-set> <XBETA=name> ...; 

	RESTRICT restriction1 [, restriction2 …]; 

	SELECTION <DETAILS=<SUMMARY | STEPS | ALL>... > <METHOD=<BACKWARD | FORWARD | FORWARDSWAP>... > <STOPHORIZON=n> ...; 

	TEST <ALL> <LM> <LR> ...; 

	VIEWSTORE <ALL> <BYVARVALUES> <CLASSVARLEVELS> ...; 

	WEIGHT <NONORMALIZE> ; 

	ZEROMODEL <LINK=<LOGISTIC | NORMAL>> <OFFSET=variable> ;
