	ARRAY <_ALL_><_NUMERIC_><_TEMPORARY_> ...;
	ATTRIB <FORMAT=format><INFORMAT=informat><LABEL='label'> ...;
	BY variables ;
	CALL routine(parameter-1<, ...parameter-n>);
	CELL cell_names / FORMAT= format ;
	COLUMNS <CHAR><MTITLE="text"><_LABEL_> ...;
	DELETE;
	DO <OVER><><> ...;
	;
	;
	ELSE <DO><IF><THEN> ...;
	END;
	GOTO label;
	IF <DO><THEN><WHEN> ...;
	INIT anchor-name [locator-name] values [locator-name values] ;
	;
	LINK label;
	PUT <_ALL_><_INFILE_><_ODS_> ...;
	RETAIN <element-list(s) <initial-value(s) |;
	ROWS <CHAR><_LABEL_><_NAME_> ...;
	;
	STOP;
	SUMBY variables ;
