PROC CPANEL <DATA=CAS-libref.data-table> <NOPART> <NOPRINT> <PRINTALL>;     
	BY variables ;

	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...;

	COMPARE <MSTAT (mstat-list)> <OUTPARM=CAS-libref.data-table> <OUTSTAT=CAS-libref.data-table> ...;

	CORRELATED effects;

	ENDOGENOUS <EQ=<BOTH | DIFF | LEVEL>> ;

	ID cross-section-id <time-series-id>;

	INSTRUMENTS <EQ=<BOTH | DIFF | LEVEL>> ;

	MODEL <AMACURDY> <ARTESTS=integer> <BIASCORRECTED> ...;

	OUTPUT <COPYVAR=variable | COPYVARS=(variables)> <OUT=CAS-libref.data-table> <PREDICTED=name | P=name> ...;

	PREDETERMINED <EQ=<BOTH | DIFF | LEVEL>> ;

	RESTRICT equation <, equation2…> ;

	TEST <ALL> <LM> <WALD> ...;
