statements.help:
PROC CQLIM <CORRB> <COVB> <COVEST=OP | HESSIAN | QML> <DATA=SAS-data-set> <ITPRINT>;     
	BOUNDS bound1 < , bound2 ...> ; 

	BY variables ;     
	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...; 

	ENDOGENOUS <CENSORED <(censored-options )>> <DISCRETE <(discrete-options)>> <DISTRIBUTION=<LOGISTIC | NORMAL>> ...; 

	FREQ variable ;     
	HETERO <LINK=<EXP | LINEAR>> <NOCONST> <SQUARE> ...; 

	INIT initvalue1 < , initvalue2 ...> ; 

	MODEL <DISCRETE <(discrete-options)>> <NOINT> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...; 

	OUTPUT <CONDITIONAL> <COPYVAR=SAS-variable-names | COPYVARS=(SAS-variable-names)> <OUT=SAS-data-set> ...; 

	RESTRICT restriction1 <, restriction2 ...> ; 

	TEST <ALL> <LM> <LR> ...; 

	WEIGHT <NONORMALIZE> ;
