PROC CSPATIALREG <APPROXIMATION=> <CORRB> <COVB> <COVEST=OP | HESSIAN | QML> <DATA=SAS-data-set> <MAXFUNC= | MAXFU=i> <MAXITER=i | MAXIT=i> <MAXTIME=r> <METHOD=CONGRA | DBLDOG | NONE... > <NONORMALIZE> <NOPRINT> <PRINTALL> <PRINTINTERNALNAMES> <PRINTTIMING> <WMAT=SAS-data-set>;     
	BOUNDS bound1 < , bound2 ...> ; 

	BY variables ;     
	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...; 

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...; 

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...; 

	INIT initvalue1 < , initvalue2 ...> ; 

	MODEL <CORRB> <NOINT> <TYPE=<LINEAR | SAR | SEM>> ...; 

	OUTPUT <COPYVAR=SAS-variable-names | COPYVARS=SAS-variable-names> <OUT=SAS-data-set> <XBETA=name> ...; 

	RESTRICT restriction1 <, restriction2 ...> ; 

	SPATIALEFFECTS <model-spatial-effect-regressors> </options>;     
	SPATIALID variable; 

	TEST <ALL> <LM> <LR> ...;
