statements.help:
PROC DOCUMENT <options>;  
	COPY path<(where-expression)><, path-2 <(where-expression-2)>,  ...>TO path</ options>;
	DELETE path<(where-expression)><, path-2 <(where-expression-2)>,  ...>< / LEVELS= ALL | value>;
	DIR <path>;
	DOC <options>; 
	DOC CLOSE ;
	HIDE path<, path-2,  ...>;
	IMPORT DATA= data-set-name<data-set-options> |  GRSEG=grseg | TEXTFILE=filename | filerefTO path</ options>;
	LINK pathTO path</ options >;
	LIST path<(where-expression)><, path-2 <(where-expression-2)>,  ...></ options>;
	MAKE path<, path-2,  ...></ options>;
	MOVE path<(where-expression)><, path-2 <(where-expression-2)>,  ...>TO path</ options >;
	NOTE path<'text'></ options>;
	OBANOTE <n>output-object<'text'></ option>;
	OBBNOTE <n>output-object<'text'></ option>;
	OBDYNAM output-object;
	OBFOOTN <n>output-object<'text'>;
	OBPAGE output-object</ options>;
	OBSTITLE <n>output-object<'text'></ options>;
	OBTEMPL output-object;
	OBTITLE <n>output-object<'text'>;
	RENAME path-1TO path-2;
	REPLAY path<(where-expression)><, path-2 <(where-expression-2)>,  ...></ options>;
	SETLABEL path 'label';
	UNHIDE path<, path-2,  ...>;QUIT ;
