PROC ENTROPY <COLLIN><CONVERGE=value><COVBEST=CROSS | GME | GMENM><DATA=SAS-data-set><DUAL><GMED><GMENM><GME><ITPRINT><MARKOV><MAXITER=n><MAXSUBITER=n><METHOD=TR | NEWRAP | NRR... ><NOPRINT><OUT=SAS-data-set><OUTCOV><OUTEST=SAS-data-set><OUTL=SAS-data-set><OUTP=SAS-data-set><OUTS=SAS-data-set><OUTSUSED=SAS-data-set><PDATA=SAS-data-set><PLOTS=ALL | FITPLOT | COOKSD... ><PURE><SDATA=SAS-data-set><SUR><VARDEF=N | WGT | DF... >;     
	BOUNDS bound1 <, bound2 ...> ;
	BY variables ;
	ID variables ;
	MODEL <ESUPPORTS=( support (prior) ... )><MARGINALS = ( variable = value, ..., variable = value)><NOINT> ...;
	PRIORS variable <support points < (priors) >> variable <support points < (priors) >> ... ;
	RESTRICT restriction1 < , restriction2 ...> ;
	TEST <LM><RAO><WALD> ...;
	WEIGHT variable ;
