PROC FMM <ABSCONV=r | ABSTOL=r> <ABSFCONV=r<n> | ABSFTOL=r<n>> <ABSFCONV=r<n> | ABSFTOL=r<n>> <COMPONENTINFO> <CORR> <COV> <COVI> <CRITERION=LOG|ILL | AIC | AICC... > <DATA=SAS-data-set> <EXCLUSION=NONE | ANY | ALL> <FCONV=r<n> | FTOL=r<n>> <FITDETAILS> <FCONV=r<n> | FTOL=r<n>> <HESSIAN> <INVALIDLOGL=r> <ITDETAILS> <MAXFUNC=n | MAXFU=n> <MAXITER=n | MAXIT=n> <MAXTIME=r> <MINITER=n | MINIT=n> <NAMELEN=number> <NOCENTER> <NOCLPRINT<=number>> <NOITPRINT> <NOPRINT> <ORDER=DATA | FORMATTED | FREQ... > <PARMSTYLE=EFFECT | LABEL> <PARTIAL=variable | MEMBERSHIP=variable> <PLOTS=ALL | NONE | DENSITY... > <SEED=n> <SINGCHOL=number> <SINGRES=number> <SINGULAR=number> <TECHNIQUE=keyword | TECH=keyword>;     
	BAYES <BETAPRIORPARMS=pair-specification> <DIAGNOSTICS=<ALL | NONE | <(keyword-list)>>> <MIXPRIORPARMS=K | MIXPRIORPARMS(value-list)> ...;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <TRUNCATE> ; 

	FREQ variable ;     
	ID variables ;     
	MODEL <ALPHA=number> <CL> <DISTRIBUTION=<BETA | BETABINOMIAL | BINARY>... > ...; 

	OUTPUT OUT=SAS-data-set | DATA=SAS-data-set PREDICTED<(COMPONENT|OVERALL)> RESIDUAL<(COMPONENT|OVERALL)> ...;     
	PERFORMANCE <CPUCOUNT=<<n> | ACTUAL>> <DETAILS> <NOTHREADS> ...; 

	PROBMODEL <ALPHA=number> <CL> <LINK=<CLOGLOG|CLL | LOGIT | LOGLOG>... > ...; 

	RESTRICT <DIVISOR=value> ; 

	WEIGHT variable;
