PROC FORMAT <options>;
	EXCLUDE entry(s);

	INVALUE <$>name <(informat-options)><value-range-set(s)>;

	PICTURE name<(format-options)>
		<value-range-set-1<(picture-1-options)>
		<value-range-set-2<(picture-2-options)>> ...>;

	SELECT entry(s);

	VALUE <$>name<(format-options)><value-range-set(s)>;
