PROC GAM <DATA=SAS-data-set> <DESCENDING> <ORDER=DATA | FORMATTED | FREQ... > <PLOTS=ALL | NONE | COMPONENTS|COMPONENT>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <DESCENDING> <ORDER=<DATA | FORMATTED | FREQ>... > <TRUNCATE<=n>> ...; 

	FREQ variable;     
	ID; 

	MODEL <ALPHA=number> <DESCENDING> <EVENT=<<'category'> | FIRST | LAST>> ...; 

	OUTPUT <LINP=> OUT=SAS-data-set <PREDICTED=> ...; 

	SCORE <DATA=SAS-data-set> <OUT=SAS-data-set> ;
