PROC GAMMOD <ALPHA=number> <DATA=CAS-libref.data-table> <ITDETAILS> <NOCLPRINT<=number>> <NOPRINT> <PLIKEOPTIONS(optimization-parameters)> <PLOTS=ALL | NONE | COMPONENTS> <SEED=number> <SINGCHOL=number> <SINGULAR=number> <SMOOTHOPTIONS(optimization-parameters)>;     
	BY <DESCENDING> <NOTSORTED> ;

	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...;

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...;

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...;

	FREQ variable;

	MODEL DESCENDING EVENT='<category>'|FIRST|LAST  ORDER=FORMATTED|FREQ|INTERNAL  ...;

	OUTPUT LINP LOWER OUT=CAS-libref.data-table ...;

	WEIGHT variable;
