PROC GCHART 
    DATA=input-data-set

    <ANNOTATE=Annotate-data-set>

    <GOUT=<libref.>output-catalog>
	<IMAGEMAP=output-data-set>;
	 BLOCK 
     chart-variable(s)
     </ options>;

	HBAR | HBAR3D | VBAR |       VBAR3D 
     chart-variable(s)
     </ options>;

	PIE | PIE3D | DONUT 
     chart-variable(s)
     </ options>;

	STAR 
     chart-variable(s)
     </ options>;

	ATTRIB  variable-list(s) 
     attribute-list(s);

	BY 
     <DESCENDING>
     variable-1
		< ... <DESCENDING> variable-n>

		<NOTSORTED>;


	FORMAT 
     variable-1
     < ...variable-n>
     <format-1>
     <variable-1< ...variable-n><format-2>>
      ...
     <DEFAULT=default-format>;

	LABEL 
     variable-1=label-1 | ' '
     <variable-n=label-n | ' '>
      ...;

	NOTE <text-arguments(s)>;

	QUIT ;

	WHERE 
     where-expression;
