PROC GDEVICE 
    <CATALOG=<libref.>SAS-catalog>
    <BROWSE>
    <NOFS>;
	ADD 
     new-device-entry
     device-parameters
     <device-row-column-parameters>;

	COPY 
     device-entry
     copy-options;

	DELETE 
     device-entry;

	LIST 
     device-entry | _ALL_ | _NEXT_ | _PREV_ | DUMP;

	MODIFY 
     device-entry
     device-parameters;

	QUIT  ;

	RENAME 
     device-entry-name
     NEWNAME=new-device-entry-name;
