PROC GEE <DATA=SAS-data-set> <DESCENDING> <NAMELEN=number> <PLOTS=ALL | HISTOGRAM | NONE>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <DESCENDING> <ORDER=<DATA | FORMATTED | FREQ>... > ; 

	ESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	FREQ | FREQUENCY variable;     
	LSMEANS <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	MISSMODEL <MAXWEIGHT=number> <TYPE=<OBSLEVEL | SUBLEVEL>> ; 

	MODEL <ALPHA=number> <DIST=<BINOMIAL | GAMMA | IGAUSSIAN>... > <LINK=<CLOGLOG | CUMCLL | CUMLOGIT>... > ...; 

	OUTPUT <LOWER=> OUT=SAS-data-set <PREDICTED=> ...; 

	REPEATED <ALPHAINIT=numbers> <CONVERGE=number> SUBJECT=subject-effect ...;     
	WEIGHT variable;
