PROC GENMOD <DATA=SAS-data-set> <DESCENDING> <NAMELEN=n> <ORDER=DATA | FORMATTED | FREQ... > <PLOTS=ALL | COOKSD | DFBETA... > <RORDER=DATA | FORMATTED | FREQ... >;     
	ASSESS <CRPANEL> LINK VAR=(effect) ...;     
	BAYES <COEFFPRIOR=<JEFFREYS | NORMAL | UNIFORM>> <DIAGNOSTICS=<ALL | NONE | <(keyword-list)>>> <DISPERSIONPRIOR=<GAMMA | IGAMMA | IMPROPER>> ...; 

	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <DESCENDING> <ORDER=<DATA | FORMATTED | FREQ>... > <TRUNCATE<=n>> ...; 

	CONTRAST <E> <SINGULAR=number> <WALD> ...; 

	DEVIANCE variable = expression ;   

	EFFECTPLOT BOX CONTOUR FIT ...;     
	ELSE <DO> <IF> <THEN> ...; 

	ESTIMATE <ALPHA=number> <E> <SINGULAR=number> ...; 

	EXACT <ALPHA=number> <CLTYPE=<EXACT | MIDP>> <ESTIMATE=<PARM | ODDS | BOTH>> ...; 

	EXACTOPTIONS <ABSFCONV=value> <ADDTOBS> <BUILDSUBSETS> ...; 

	FREQ | FREQUENCY variable;     
	FWDLINK variable = expression ;     
	IF <DO> <THEN> <WHEN> ...; 

	INVLINK variable = expression ;     
	LSMEANS <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	LSMESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	MODEL <AGGREGATE= (variable-list) | AGGREGATE= variable | AGGREGATE> <ALPHA=number> <CICONV=number> ...; 

	OUTPUT <DFBETA=> <DFBETAS=> OUT=SAS-data-set ...;     
	REPEATED <ALPHAINIT=numbers> <CONVERGE=number> SUBJECT=subject-effect ...;     
	SLICE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	STORE <LABEL='label'> ; 

	STRATA <MISSING> ; 

	VARIANCE variable = expression ;   

	WEIGHT | SCWGT variable;     
	ZEROMODEL <LINK=<CLOGLOG | LOGIT | PROBIT>> ;
