PROC GKPI 
            <MODE= BASIC | MODERN | RAISED>;                   
	DIAL                 ACTUAL=value BOUNDS=bounds-list
               </ options>;

	HBULLET | BULLET                 ACTUAL=value BOUNDS=bounds-list
               </ options>;

	VBULLET                 ACTUAL=value BOUNDS=bounds-list
               </ options>;

	HSLIDER | SLIDER                 ACTUAL=value BOUNDS=bounds-list
               </ options>;

	VSLIDER                 ACTUAL=value BOUNDS=bounds-list
               </ options>;

	HTRAFFICLIGHT                 ACTUAL=value BOUNDS=bounds-list
               </ options>;

	VTRAFFICLIGHT |                   TRAFFICLIGHT  ACTUAL=value

		BOUNDS=bounds-list
               </ options>;

	SPEEDOMETER                 ACTUAL=value BOUNDS=bounds-list
               </ options>;

	QUIT ;
