PROC GLIMMIX <ABSPCONV=r> <ASYCORR> <ASYCOV> <CHOLESKY> <DATA=SAS-data-set> <EMPIRICAL=CLASSICAL|HCO | DF|HC1 | MBN... > <EXPHESSIAN> <FDIGITS=r> <GRADIENT> <HESSIAN> <INFOCRIT=NONE | PQ | Q> <INITGLM> <INITITER=number> <ITDETAILS> <LIST> <MAXLMMUPDATE=number> <METHOD=RSPL | MSPL | RMPL... > <NAMELEN=number> <NOBOUND> <NOBSDETAIL> <NOCLPRINT<=number>> <NOFIT> <NOINITGLM> <NOITPRINT> <NOPROFILE> <NOREML> <ODDSRATIO> <ORDER=DATA | FORMATTED | FREQ... > <OUTDESIGN<(options)><=SAS-data-set>> <PCONV=r> <PLOTS=ALL | ANOMPLOT|ANOM | BOXPLOT... > <PROFILE> <SCOREMOD> <SCORING=number> <SINGCHOL=number> <SINGRES=number> <SINGULAR=number> <SUBGRADIENT<=SAS-data-set>>;     
	ABORT <ABEND> <CANCEL> <CANCEL FILE> ...;     
	ARRAY arrayname [ dimensions ] <$> <variables-and-constants> ; 

	BY <DESCENDING> <NOTSORTED> ; 

	CALL routine(parameter-1<, ...parameter-n>); 

	CLASS <REF=<'<level>' | FIRST | LAST>> <TRUNCATE> ; 

	CODE <CATALOG=library.catalog.entry.typeCAT=library.catalog.entry.type> <DUMMIES> <NODUMMIES> ...; 

	CONTRAST <BYCATEGORY> <CHISQ> <DF=number> ...; 

	COVTEST DIAGG DIAGR GLM ...;     
	DELETE; 

	DO <OVER> <> <> ...; 

	EFFECT EFFECT-NAME=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	ELSE <DO> <IF> <THEN> ...; 

	END;     
	ESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	FREQ variable;     
	GOTO label;     
	ID variables ; 

	IF <DO> <THEN> <WHEN> ...; 

	LINK statement-label;     
	LSMEANS <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	LSMESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	MODEL <DESCENDING> <EVENT=> <ORDER=> ...; 

	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r <n> | ABSFTOL=r<n>> <ABSGCONV= | ABSGTOL=r <n>> ...;     
	; 

	OUTPUT OUT=SAS-data-set PREDICTED STDERR ...; 

	PARMS <HOLD=value-list> <LOWERB=value-list> <NOBOUND> ...; 

	PUT <_PAGE_> ;  

	RANDOM <ALPHA=number> <CL> <G> ...; 

	RETURN;     
	; 

	SLICE <ADJDFE=<SOURCE | ROW>> <NOF> <> ...; 

	STOP ;     
	STORE <LABEL='label'> <OUT=>item-store-name' ;     
	WEIGHT variable;  

	WHEN <DO> ;
