PROC GLM <ALPHA=α> <DATA=SAS-data-set> <MANOVA> <MULTIPASS> <NAMELEN=n> <NOPRINT> <ORDER=DATA | FORMATTED | FREQ... > <OUTSTAT=SAS-data-set> <PLOTS=ALL | ANCOVAPLOT | ANOMPLOT... >;     
	ABSORB variables ;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <TRUNCATE> ; 

	CONTRAST <E> <E=effect> <ETYPE=<1 | 2 | 3>... > ...; 

	ESTIMATE <DIVISOR=number> <E> <SINGULAR=number> ...; 

	FREQ variable;     
	ID variables ;     
	LSMEANS <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=α> <AT variable=value> ...;     
	MANOVA <E=effect> <H=<<effects> | INTERCEPT | _ALL_>> <M=equation,...,equation | (row-of-matrix,...,row-of-matrix)> ...; 

	MEANS <ALPHA=α> <BON> <CLM> ...; 

	MODEL <ALIASING> <ALPHA=α> <CLI> ...; 

	OUTPUT COOKD= COVRATIO= DFFITS= ...; 

	RANDOM <Q> <TEST> ; 

	REPEATED CONTRAST<(ordinal-reference-level)> HELMERT IDENTITY ...;     
	STORE <LABEL='label'> ; 

	TEST E=effect <ETYPE=<1 | 2 | 3>... > H=effects ...;     
	WEIGHT variable;
