PROC GLMPOWER <DATA=SAS-data-set> <ORDER=DATA | FORMATTED | FREQ... > <PLOTONLY>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS variables ;     
	CONTRAST <SINGULAR=number> ; 

	MANOVA M=equation,...,equation | (row-of-matrix,...,row-of-matrix) <ORTH> ; 

	MODEL dependents = independents ;  

	PLOT INTERPOL=JOIN|NONE  KEY=BYCURVE|BYFEATURE|ONCURVES  MARKERS=ANALYSIS|COMPUTED|NICE...  ...;     
	POWER <ALPHA=number-list> <CORRMAT=name-list> <CORRS=name-list> ...; 

	REPEATED factor-specification;     
	WEIGHT variable;
