PROC GLMSELECT <DATA=SAS-data-set> <MAXMACRO=n> <NAMELEN=n> <NOPRINT> <OUTDESIGN <(options)><=SAS-data-set>> <PARMLABELSTYLE=INTERLACED | SEPARATE | SEPARATECOMPACT> <PLOTS=ALL | ASEPLOT | CANDIDATESPLOT... > <SEED=number> <TESTDATA=SAS-data-set> <VALDATA=SAS-data-set>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <CPREFIX=n> <DELIMITER=quoted character> <SHOW> ...; 

	CODE <CATALOG=library.catalog.entry.typeCAT=library.catalog.entry.type> <DUMMIES> <NODUMMIES> ...; 

	EFFECT EFFECT-NAME=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	FREQ variable ;     
	MODEL <CVDETAILS=<ALL | COEFFS | CVPRESS>> <CVMETHOD=<BLOCK | RANDOM | SPLIT>... > <DETAILS=<ALL | STEPS | SUMMARY>> ...; 

	MODELAVERAGE <ALPHA=α> <DETAILS> <NSAMPLES=n> ...; 

	OUTPUT OUT=SAS data set <PREDICTED> <RESIDUAL> ...; 

	PARTITION <FRACTION(<TEST=fraction> <VALIDATE=fraction>)> <ROLEVAR= | ROLE=variable(<TEST='value'> <TRAIN='value'> <VALIDATE='value'>)> ; 

	PERFORMANCE <BUILDSSCP=<FULL | INCREMENTAL>> <DETAILS> ; 

	SCORE <DATA=SAS data set> <PREDICTED> <RESIDUAL> ...; 

	STORE <LABEL=> OUT=item-store-name ;     
	WEIGHT variable ;
