PROC GRADAR <DATA=input-data-set>
<GOUT=<libref.>output-catalog>
<ANNOTATE=Annotate-data-set>;
	CHART chart-variable</ options>;

	ATTRIB  variable-list(s) attribute-list(s);

	BY <DESCENDING>variable-1
		< ... <DESCENDING> variable-n>
		<NOTSORTED>;

	FORMAT variable-1< ...variable-n><format-1><variable-1< ...variable-n><format-2>> ...<DEFAULT=default-format>;

	LABEL variable-1=label-1 | ' '<variable-n=label-n | ' '> ...;

	NOTE <text-arguments(s)>;

	QUIT ;

	WHERE where-expression;
