PROC                GREPLAY 
	<BYLINE>

	<CC=color-map-catalog>

	<CMAP=color-map-entry-type>

	<GOUT=<libref.>output-catalog>

	<IGOUT=<libref.>input-catalog>

	<IMAGEMAP=output-data-set>

	<NOBYLINE>

	<NOFS>

	<TC=template-catalog>

	<TEMPLATE=template-entry>;



		? 
                  CC | CMAP | DEVICE | GOUT | IGOUT | TC | TEMPLATE;

		BYLINE ;

		CC 
                  color-map-catalog;

		CCOPY 
                  <color-map-catalog.>color-map-entry<.CMAP>;

		CDEF 
                  color-map-entry

                     color-number / from-color : to-color


			<color-number / from-color : to-color>

			< ...>

			<DES="description">                   ;

		CDELETE 
                  color-map-entry(s) | _ALL_;

		CMAP 
                  color-map-entry;

		COPY 
                  entry-id(s) | _ALL_;

		DELETE 
                  entry-id(s) | _ALL_                   ;

		DEVICE 
                  device-name;

		GOUT 
                  <libref.>output-catalog;

		GROUP 
                  entry-id(s);

		IGOUT 
                  <libref.>input-catalog;

		LIST 
                  CC | CMAP | IGOUT | TC | TEMPLATE;

		MODIFY 
                  entry-id / <BYLINE="character-string"> <NAME="new-entry-name"> <DES="description">;

		MOVE 
                  entry-id-1 AFTER | BEFORE entry-id-2;

		NOBYLINE ;

		PREVIEW 
                  template-entry(s) | _ALL_                   ;

		QUIT ;

		REPLAY 
                  entry-id(s) | _FIRST_ | _LAST_ | _ALL_                   ;

		TC 
                  template-catalog;

		TCOPY 
                  <template-catalog.>template-entry<.TEMPLATE>;

		TDEF 
                  template-entry

                     panel-number / <panel-options>


			<panel-number / <panel-options>>

			< ...>

			<DES="description">                   ;

		TDELETE 
                  template-entry(s) | _ALL_;

		TEMPLATE 
                  template-entry;

		TREPLAY 
                  select-pair(s);
