statements.help:
PROC HMM <DATA=SAS-data-set> <OUTSTAT=SAS-data-set>;     
	DECODE <OUT=SAS-data-set> ;

	ESTIMATE <OUT=SAS-data-set> ;

	EVALUATE <OUT=SAS-data-set> ;

	FILTER <OUT=SAS-data-set> ;

	FORECAST <ALPHA=number> <BACK=number> <LEAD=number> ...;

	ID <SECTION=variable> TIME=variable ;

	INITIAL equation, …, equation;

	MODEL <ESTISPV> <METHOD=<MAP | ML>> <NSTATE=number> ...;

	OPTIMIZE <ALGORITHM=<ACTIVESET | INTERIORPOINT>> <FEASTOL=number> <MAXITER=number> ...;

	PRIOR ISPV MUSIGMA TPM ...;

	SCORE <INMODEL=SAS-data-set> <OUTMODEL=SAS-data-set> ;

	SMOOTH <OUT=SAS-data-set> ;
