PROC HPFEVENTS <DATA=SAS-data-set> <LEAD=n> <MAXERROR=number> <SORTNAMES>;     
	BY <DESCENDING> <NOTSORTED> ; 

	EVENTCOMB <LABEL=’SAS-label’> <RULE=<ADD | MAX | MIN>... > ; 

	EVENTDATA <CONDENSE> <IN=SAS-data-set> <OUT=SAS-data-set> ...; 

	EVENTDEF <AFTER=( <DURATION=value> <SLOPE=value> )> <BEFORE=( <DURATION=value> <SLOPE=value> )> <LABEL=’SAS-label’> ...; 

	EVENTDUMMY <OUT=SAS-data-set> ; 

	EVENTGROUP <LABEL=’SAS-label’> ; 

	EVENTKEY <variable=> event-keyword < / options > ;     
	ID <ALIGN=<BEGINNING|BEG|B | MIDDLE|MID|M | ENDING|END|E>> <END=option> <FORMAT=format> ...; 

	VAR variables ;
