PROC HPFMM <ABSCONV=r | ABSTOL=r> <ABSFCONV=r<n> | ABSFTOL=r<n>> <ABSFCONV=r<n> | ABSFTOL=r<n>> <COMPONENTINFO> <CORR> <COV> <COVI> <CRITERION=LOG | AIC | AICC... > <DATA=SAS-data-set> <EXCLUSION=NONE | ANY | ALL> <FCONV=r<n> | FTOL=r<n>> <FITDETAILS> <GCONV=r<n> | GTOL=r<n>> <HESSIAN> <INVALIDLOGL=r> <ITDETAILS> <MAXFUNC=n | MAXFU=n> <MAXITER=n | MAXIT=n> <MAXTIME=r> <MINITER=n | MINIT=n> <NAMELEN=number> <NOCENTER> <NOCLPRINT<=number>> <NOITPRINT> <NOPRINT> <ORDER=DATA | FORMATTED | FREQ... > <PARMSTYLE=EFFECT | LABEL> <PARTIAL=variable | MEMBERSHIP=variable> <PLOTS=ALL | NONE | DENSITY... > <SEED=n> <SINGCHOL=number> <SINGRES=number> <SINGULAR=number> <TECHNIQUE=keyword | TECH=keyword>;     
	BAYES <BETAPRIORPARMS=pair-specification> <DIAGNOSTICS=<ALL | NONE | <(keyword-list)>>> <MIXPRIORPARMS=K | MIXPRIORPARMS(value-list)> ...;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <DESCENDING> <ORDER=<DATA | FORMATTED | FREQ>... > <REFERENCE=<'<level>' | FIRST | LAST>> ...; 

	FREQ variable ;     
	ID variables ;     
	MODEL <ALPHA=number> <CL> <DISTRIBUTION=<BETA | BETABINOMIAL | BINARY>... > ...; 

	OUTPUT OUT=SAS-data-set PREDICTED<(COMPONENT|OVERALL)>PRED<(COMPONENT|OVERALL)>MEAN<(COMPONENT|OVERALL)> RESIDUAL<(COMPONENT|OVERALL)> | RESID<(COMPONENT|OVERALL)> ...;     
	PERFORMANCE <COMMIT=n> <DETAILS> <GRIDHOST="name"> ...; 

	PROBMODEL <ALPHA=number> <CL> <LINK=<CLOGLOG | LOGIT | LOGLOG>... > ...; 

	RESTRICT <DIVISOR=value> ; 

	WEIGHT variable;
