PROC HPFUCMSPEC <LABEL=SAS-label><NAME=SAS-name><REPOSITORY=SAS-catalog-name | SAS-file-reference>;     
	AUTOREG <NOEST=<RHO | VARIANCE>><RHO=value><VARIANCE=value> ...;
	BLOCKSEASON <BLOCKSIZE=<RHO | VARIANCE>><NBLOCKS=integer><NOEST> ...;
	CYCLE <NOEST=<PERIOD | RHO | VARIANCE>><PERIOD=value><RHO=value> ...;
	DEPLAG <LAGS=order><NOEST><PHI=value ...> ...;
	FORECAST <(SYMBOL|VAR)= variable><TRANSFORM=<NONE | LOG | SQRT>... > ;
	INPUT <DELAY=order><DIF=order | ( order1, order2, ...)><PREDEFINED=<LINEAR | QUADRATIC | CUBIC>... > ...;
	IRREGULAR <NOEST><VARIANCE=value> ;
	LEVEL <NOEST><VARIANCE=value> ;
	SEASON <LENGTH=integer><NOEST><TYPE=<DUMMY | TRIG>> ...;
	SLOPE <NOEST><VARIANCE=value> ;
