PROC HPMIXED <DATA=SAS-data-set> <INFOCRIT=NONE | PQ | Q> <ITDETAILS> <MAXCLPRINT=number> <METHOD=REML> <MMEQ> <NAMELEN=number> <NOCLPRINT<=number>> <NOFIT> <NOINFO> <NOITPRINT> <NOPRINT> <NOPROFILE> <ORDER=DATA | FORMATTED | FREQ... > <SINGCHOL=number> <SINGRES=number> <SINGULAR=number>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <TRUNCATE> ; 

	CONTRAST <CHISQ> <DF=number> <E> ...; 

	EFFECT EFFECT-NAME=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	ESTIMATE <ALPHA=number> <CL> <DIVISOR=number> ...; 

	ID variables ;     
	LSMEANS <CL> <COV> <E> ...; 

	MODEL <ALPHA=number> <CL> <DDF=value-list> ...; 

	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r <n> | ABSFTOL=r<n>> <ABSGCONV= | ABSGTOL=r <n>> ...;     
	OUTPUT OUT=SAS-data-set PREDICTED= STDERR= ...;     
	PARMS <HOLD=value-list> <NOITER> <PARMSDATA=SAS-data-set> ...; 

	RANDOM <ALPHA=number> <CL> <GROUP=effect> ...; 

	REPEATED <GROUP=effect> <R<=value-list>> <RC<=value-list>> ...; 

	TEST <CHISQ> <E> <HTYPE=value-list> ...; 

	WEIGHT variable;
