PROC HPNLMOD <ABSCONV= | ABSTOL=r> <ABSFCONV=r<n> | ABSFTOL=r<n>> <ABSGCONV=r<n> | ABSGTOL=r<n>> <ALPHA=α> <CORR> <COV> <DATA=SAS-data-set> <DF=n> <ECORR> <ECOV> <FCONV=r<n> | FTOL=r<n>> <FMTLIBXML=file-ref> <GCONV=r | GTOL=r> <MAXFUNC=n | MAXFU=n> <MAXITER=n | MAXIT=n> <MAXTIME=r> <MINITER=n | MINIT=n> <NOITPRINT> <NOPRINT> <OUT=SAS-data-set> <SINGULAR=number> <TECHNIQUE=CONGRA | DBLDOG | LEVMAR... > <XMLFORMAT=filename>;     
	ABORT;     
	BOUNDS constraint < , constraint . . . > ;     
	BY <DESCENDING> <NOTSORTED> ; 

	CALL name [ ( expression [, expression ... ] ) ]; 

	DELETE;     
	DO[variable = expression;     
	ELSE <DO> <IF> <THEN> ...; 

	END;     
	ESTIMATE <ALPHA=α> <DF=d> ; 

	GOTO statement_label;     
	IF <DO> <THEN> <WHEN> ...; 

	LINK statement_label;     
	MODEL <binary(p)> <binomial(n, p)> <gamma(a, b)> ...; 

	;     
	PARAMETERS <BEST=i > 0> <PDATA=SAS-data-set | DATA=SAS-data-set> <START=value | DEFSTART=value> ...;     
	PERFORMANCE <COMMIT=n> <CPUCOUNT=<ACTUAL | <num>>> <DATASERVER=“name”> ...; 

	PREDICT <ALPHA=α> <DF=d> <LOWER=name> ...; 

	PUT <_PDV_> ;   

	RESTRICT restriction1 < , restriction2 . . . > ;     
	RETURN;     
	; 

	STOP ;     
	SUBSTR( variable, index, length )= expression;     
	WHEN <DO> ;
