PROC HPQUANTSELECT <DATA=SAS-data-set> <MAXMACRO=n> <NPRINT> <OUTDESIGN<(options)=><SAS-data-set>> <PLOTS=ALL | ACL|ACLPLOT | COEF|COEFFICIENTS|COEFFICIENTPANEL... > <SEED=number> <TESTDATA=SAS-data-set> <VALDATA=SAS-data-set>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <CPREFIX=n> <DELIMITER='c'> <SHOW> ...; 

	CODE <CATALOG=library.catalog.entry.typeCAT=library.catalog.entry.type> <FILE= filename> <FORMAT=format> ...; 

	EFFECT EFFECT-NAME=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	MODEL <CLB> <INCLUDE=n | INCLUDE=single-effect | INCLUDE=(effects)> <NOINT> ...; 

	OUTPUT <COPYVAR=variable | COPYVARS=(variables)> <LLCM <=name>> <OUT=SAS-data-set> ...; 

	PARTITION <FRACTION(<TEST=fraction> <VALIDATE=fraction>)> <ROLEVAR= | ROLE=variable(<TEST='value'> <TRAIN='value'> <VALIDATE='value'>)> ; 

	PERFORMANCE <COMMIT=n> <DETAILS> <GRIDHOST="name"> ...; 

	SELECTION <BACKWARD> <FORWARD> <NONE> ...; 

	WEIGHT variable ;
