PROC HPREG <ALPHA=number> <DATA=SAS-data-set> <FMTLIBXML=file-ref> <NAMELEN=number> <NOCLPRINT< =number >> <NOPRINT> <SEED=number>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <DESCENDING> <ORDER=<DATA | FORMATTED | FREQ>... > <REF=<<'level'> | FIRST | LAST>> ...; 

	CODE <CATALOG=library.catalog.entry.type> <DUMMIES> <NODUMMIES> ...; 

	FREQ variable ;     
	ID variables ;     
	MODEL <CLB> <NOINT> <ORDERSELECT> ...; 

	OUTPUT <LINP> OUT=SAS-data-set | DATA=SAS-data-set PREDICTED ...;     
	PARTITION <FRACTION(<TEST=fraction> <VALIDATE=fraction>)> <ROLEVAR= | ROLE=variable(<TEST=’value’> <TRAIN=’value’> <VALIDATE=’value’>)> ; 

	PERFORMANCE <COMMIT=n> <CPUCOUNT=<ACTUAL | <num>>> <DATASERVER=“name”> ...; 

	SELECTION <ADAPTIVE <(GAMMA=nonnegative number)>> <CHOOSE=<AIC | AICC | SBC>> <METHOD=<BACKWARD | FORWARD | FORWARDSWAP>... > ...; 

	WEIGHT variable;
