PROC ICPHREG <ALPHA=number> <DATA=SAS-data-set> <ITHISTORY> <NAMELEN=n> <NLOPTIONS(options)> <NOPRINT> <NOTHREADS> <PLOTS=CUMHAZ | HAZARD | INTERVAL... > <SINGULAR=number | EPSILON=number> <THREADS=n | NTHREADS=n>;     
	BASELINE COVARIATES=SAS-data-set OUT=SAS-data-set TIMELIST=list ...;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <CPREFIX=n> <DESCENDING> <LPREFIX=n> ...; 

	FREQ <NOTRUNCATE> ; 

	HAZARDRATIO <ALPHA=number> <AT (variable=ALL | REF | list <...variable=ALL | REF| list> )> <DIFF=<DISTINCT | PAIRWISE  | REFERENCE>> ...; 

	MODEL <ALPHA=value> <CORRB> <COVB> ...; 

	OUTPUT INTERVAL= OUT=SAS-data-set RESDEV= ...; 

	STRATA <MISSING> ; 

	TEST <E> <E1> <E2> ...;
