PROC KRIGE2D <DATA=SAS-data-set> <NOPRINT> <OUTEST=SAS-data-set> <OUTNBHD=SAS-data-set> <PLOTS=ALL | EQUATE | NONE... > <SINGULARMSG=number>;     
	BY <DESCENDING> <NOTSORTED> ; 

	COORDINATES <XCOORD= (variable-name)> <YCOORD= (variable-name)> ;     
	GRID <GRIDDATA=SAS-data-set> <XCOORD= (variable-name)> <YCOORD= (variable-name)> ...; 

	MODEL <ANGLE=angle | ANGLE=(angle-1,...,angle-k)> <FORM=<SPHERICAL|SPH | EXPONENTIAL|EXP | GAUSSIAN|GAUSS>... > <MDATA=SAS-data-set> ...; 

	PREDICT <MAXPOINTS=number> <MINPOINTS=number> <NODECREMENT> ...; 

	RESTORE <INFO <( info-options )>> ;
