PROC LIFEREG <COVOUT> <DATA=SAS-data-set> <GOUT=graphics-catalog> <INEST=SAS-data-set> <NAMELEN=n> <NOPRINT> <ORDER=DATA | FORMATTED | FREQ... > <OUTEST=SAS-data-set> <PLOTS=NONE | PROBPLOT> <XDATA=SAS-data-set>;     
	BAYES <COEFFPRIOR=<NORMAL | UNIFORM>> <DIAGNOSTICS=<ALL | NONE | <(keyword-list)>>> <EXPSCALEPRIOR=<GAMMA | IMPROPER>> ...; 

	BY <DESCENDING> <NOTSORTED> ; 

	CLASS variables ;     
	EFFECTPLOT BOX CONTOUR FIT ...;     
	ESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	INSET CONFIDENCE DIST INTERVAL ...; 

	LSMEANS <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	LSMESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	MODEL <ALPHA=number> <CONVERGE=value> <CONVG=value> ...; 

	OUTPUT <CDF=variable> <CENSORED=variable> <OUT=SAS-data-set> ...; 

	PROBPLOT <ANNOTATE=SAS-data-set> <CAXIS=color> <CCENSOR=color> ...; 

	SLICE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	STORE <LABEL='label'> ; 

	TEST <CHISQ> <DDF=value-list> <E> ...; 

	WEIGHT variable ;
