PROC LMIXED <DATA=CAS-libref.data-table> <DMMETHOD=DENSE | SPARSE> <MAXCLPRINT=number> <METHOD=ML | REML> <MMEQ> <NAMELEN=number> <NOCLPRINT<=number>> <NOINFO> <NOITPRINT> <NOPRINT> <NOPROFILE> <RANKS> <SIMPLE> <SINGCHOL=number> <SINGRES=number> <SINGULAR=number> <TIMING>;     
	BLUP <ITPRINT=number> <MAXITER=number> <OUT=CAS-libref.data-table> ...; 

	BY variables;     
	CLASS <DESCENDING> <MISSING> <ORDER=<DATA | FORMATTED | FREQ>... > ...; 

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...; 

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...; 

	EFFECT <DETAILS> NAME=COLLECTION|MULTIMEMBER|MM|POLYNOMIAL|POLY...  <NOEFFECT> ...;     
	MODEL <ALPHA=α> <DDFM=<NONE | RESIDUAL>> <NOINT> ...; 

	OPTIMIZATION <ABSCONV=r> <ABSFCONV=r> <ABSGCONV=r> ...; 

	OUTPUT <ALLSTATS> <ALPHA=number> <OUT=CAS-libref.data-table> ...; 

	PARMS <HOLD<=order-list> | EQCONS<=order-list> | HOLD<=ALL> | EQCONS<=ALL>> <LOWERB=value-list> <NOITER> ...; 

	RANDOM <ALPHA=α> <CL> <SOLUTION> ...; 

	WEIGHT variable;
