PROC LOGISTIC <ALPHA=number> <COVOUT> <DATA=SAS-data-set> <DESCENDING> <EXACTONLY> <EXACTOPTIONS (options)> <INEST=SAS-data-set> <INMODEL=SAS-data-set> <MULTIPASS> <NAMELEN=n> <NOCOV> <NOPRINT> <ORDER=DATA | FORMATTED | FREQ... > <OUTDESIGN=SAS-data-set> <OUTDESIGNONLY> <OUTEST=SAS-data-set> <OUTMODEL=SAS-data-set> <PLOTS=ALL | DFBETAS | DPC... > <ROCOPTIONS (options)> <SIMPLE> <TRUNCATE>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <CPREFIX=n> <DESCENDING> <LPREFIX=n> ...; 

	CODE <CATALOG=library.catalog.entry.typeCAT=library.catalog.entry.type> <DUMMIES> <NODUMMIES> ...; 

	CONTRAST <ALPHA=number> <E> <ESTIMATE=<PARM | EXP | BOTH>... > ...; 

	EFFECT EFFECT-NAME=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	EFFECTPLOT BOX CONTOUR FIT ...;     
	ESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	EXACT <ALPHA=number> <CLTYPE=<EXACT | MIDP>> <ESTIMATE=<PARM | ODDS | BOTH>> ...; 

	EXACTOPTIONS <ABSFCONV=value> <ADDTOBS> <BUILDSUBSETS> ...; 

	FREQ variable ;     
	ID variable<variable,...> ;     
	LSMEANS <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	LSMESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	MODEL DESCENDING EVENT=<'category'>|FIRST|LAST  ORDER=DATA|FORMATTED|FREQ...  ...;     
	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r <n> | ABSFTOL=r<n>> <ABSGCONV= | ABSGTOL=r <n>> ...;     
	ODDSRATIO <AT(covariate=value-list | REF | ALL<...covariate=value-list | REF | ALL>)> <CL=<WALD | PL | BOTH>> <DIFF=<REF | ALL>> ...; 

	OUTPUT <ALPHA=number> <C=name> OUT=SAS-data-set ...;     
	ROC <LINK=<LOGIT | NORMIT | CLOGLOG>> <NOINT> <NOOFFSET> ...; 

	ROCCONTRAST ADJACENTPAIRS <E> REFERENCE<(MODEL | 'roc-label')> ...;     
	SCORE <ALPHA=number> <CLM> <CUMULATIVE> ...; 

	SLICE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	STORE <LABEL='label'> <<OUT=>item-store-name> ; 

	STRATA <CHECKDEPENDENCY=<NONE | COVARIATES | ALL>> <MISSING> <NOSUMMARY> ...; 

	TEST <PRINT> ;  

	UNITS <DEFAULT=list> ; 

	WEIGHT <NORMALIZE> ;
