PROC LOGSELECT <ABSCONV=r | ABSTOL=r> <ABSFCONV= r <n> | ABSFTOL=r <n>> <ABSGCONV=r <n> | ABSGTOL=r <n>> <ABSXCONV=r <n> | ABSXTOL=r <n>> <ALPHA=number> <CORRB> <COVB> <DATA=CAS-libref.data-table> <FCONV2=r <n> | FTOL2=r <n>> <FCONV=r <n> | FTOL=r <n>> <GCONV2=r <n> | GTOL2=r <n>> <GCONV=r <n> | GTOL=r <n>> <ITHIST> <LASSORHO=r> <LASSOSTEPS=n> <LASSOTOL=r> <MAXFUNC=n | MAXFU=n> <MAXITER=n | MAXIT=n> <MAXOPTBATCH=number> <MAXTIME=r> <MINITER=n | MINIT=n> <NOCHECK> <NOCLPRINT< =number >> <NORMALIZE=YES | NO> <NOSTDERR> <PARTFIT> <STB> <TECHNIQUE=CONGRA | DBLDOG | LEVMAR... > <XCONV=r | XTOL=r>;     
	BY variables ; 

	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...; 

	CODE <COMMENT> <FILE=filename> <FORMATWIDTH=width> ...; 

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...; 

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...; 

	EFFECT NAME=COLLECTION|MULTIMEMBER|MM|POLYNOMIAL|POLY...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	FREQ variable ;     
	MODEL DESCENDING EVENT=<'category'>|FIRST|LAST  ORDER=FORMATTED|FREQ|INTERNAL  ...;     
	OUTPUT <ALL> <ALPHA=number> <OUT=CAS-libref.data-table> ...; 

	PARTITION <FRACTION(< TEST=fraction > < VALIDATE=fraction > < SEED=number >)> <ROLE=|ROLEVAR=variable (< TEST='value' > < TRAIN='value' > < VALIDATE='value' >)> ;     
	SELECTION <ADAPTIVE <(GAMMA=nonnegative number)>> <CHOOSE=<AIC | AICC | SBC>> <METHOD=<BACKWARD | FORWARD | FORWARDSWAP>... > ...; 

	WEIGHT variable;
