PROC MBC <COVSTRUCT=EEE | EEI | EEV... > <CRITERION=AIC | AICC | BIC... > <DATA=CAS-libref.data-table> <ITHIST> <ITHIST=DETAILS | NONE | SUMMARY> <MAXITER=number> <MCRITERION=number> <NCLUSTERS=number | (number <number …number>) | (number to number <by number>)> <NIT=KMEANS | RANDOM> <NOISE=YES | NO> <SEED=integer> <SINGPARM=number> <SINGULAR=number> <TECHNIQUE=EM | CEM> <TOPMODELS=number>;     
	BY variables;

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...;

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...;

	INIT variables;

	OUTPUT <ALL | ALLSTAT> <COPYVAR=variable | COPYVARS=(variables)> <OUT=CAS-libref.data-table> ...;

	STORE <OUT=CAS-libref.data-table> ;

	VAR variables;
