PROC MCMC <ACCEPTTOL=n> <ALG=HMC | NORMAL|N | NUTS... > <AUTOCORLAG=n> <BINARYJOINT> <DATA=SAS-data-set> <DIAGNOSTICS=NONE | <(keyword-list)>> <DIC> <DISCRETE=BINNING | GEO> <INF=value> <INIT=(keyword-list)> <JOINTMODEL> <LIST> <LISTCODE> <MAXINDEXPRINT=number | ALL | MAXIPRINT=number | ALL> <MAXSUBVALUEPRINT=number | ALL | MAXSVPRINT=number | ALL> <MAXTUNE=n> <MCHISTORY=BRIEF | DETAILED | NONE> <MINTUNE=n> <MISSING=ALLCASE|AC | COMPLETECASE|CC> <MONITOR= (symbol-list)> <NBI=n> <NMC=n> <NOLOGDIST> <NTHREADS=n> <NTU=n> <OUTPOST=SAS-data-set> <PLOTS=ALL | AUTOCORR|ACF | DENSITY |D|KERNEL|K... > <PROPCOV=IND | CONGRA | DBLDOG... > <REOBSINFO <(display-options)>> <SCALE=value> <SEED=n> <SIMREPORT=n> <SINGDEN=value> <STATISTICS=ALL |  | CORR... > <TARGACCEPT=value> <TARGACCEPTI=value> <THIN=n> <TRACE> <TUNEWT=value>;     
	ABORT <ABEND> <CANCEL> <CANCEL FILE> ...;     
	ARRAY arrayname [ dimensions ] <$> <variables-and-constants> ; 

	BEGINCNST ;     
	BEGINHYPER ;     
	BEGINNODATA ; | BEGINPRIOR ; 

	BY <DESCENDING> <NOTSORTED> ; 

	CALL routine(parameter-1<, ...parameter-n>); 

	DELETE;     
	DO <OVER> <> <> ...; 

	ELSE <DO> <IF> <THEN> ...; 

	END;     
	ENDCNST ; 

	ENDHYPER ;     
	ENDNODATA | ENDPRIOR ;     
	GOTO label; 

	HYPERPRIOR <beta(<a=>α, <b=>β)> <binary(<prob|p=>p)> <binomial(<n=>n, <prob|p=>p)> ...;     
	IF <DO> <THEN> <WHEN> ...; 

	LINK statement-label;     
	MODEL <beta(<a=>α, <b=>β)> <binary(<prob|p=>p)> <binomial(<n=>n, <prob|p=>p)> ...;     
	; 

	PARMS <NORMAL> <SLICE> <T<(df)>> ...; 

	PREDDIST <COVARIATES=SAS-data-set> <NSIM=n> <OUTPRED=SAS-data-set> ...; 

	PRIOR <beta(<a=>α, <b=>β)> <binary(<prob|p=>p)> <binomial(<n=>n, <prob|p=>p)> ...;     
	PUT <_PAGE_> ; 

	RANDOM <beta(<a=>α, <b=>β)> <binary(<prob|p=>p)> <gamma(<shape|sp=>α, scale|s=λ) | gamma(<shape|sp=>α, isscale|is=λ)> ...; 

	RETURN;     
	; 

	STOP ;     
	UDS subroutine-name ( subroutine-argument-list) ;     
	WHEN <DO> ;
