PROC MDC <COVOUT> <DATA=SAS-data-set> <OUTEST=SAS-data-set>;     
	BOUNDS bound1 <, bound2 ...> ;

	BY variables ;

	CLASS variables ;

	ID variable ;

	MODEL <CHOICE=(variables) | CHOICE=(variable numbers)> <CONVERGE=number> <HALTONSTART=number> ...;

	NEST <LEVEL(level-number )= ( choices@choice, ...)> ;

	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r[n] | ABSFTOL=r[n> <ABSGCONV=r[n] | ABSGTOL=r[n> ...;

	OUTPUT <OUT=SAS-data-set> <PRED=variable name> <XBETA=variable name> ...;

	RESTRICT restriction1 < , restriction2 ...> ;

	TEST <ALL> <LM> <WALD> ...;

	UTILITY U(level < , choices> )= variables ;
