PROC MODEL <BLOCK> <DATA=SAS-data-set> <DETAILS> <FLOW> <GRAPH> <LIST> <LISTALL> <LISTCODE> <LISTDEP> <LISTDER> <MAXERRORS=n> <MEMORYUSE> <MODEL=model-name | (model-list)> <NDEC=n> <NOPRINT> <NOSTORE> <OUTMODEL=model-name> <OUTPARMS=SAS-data-set> <PARMSDATA=SAS-data-set> <PLOTS=ALL | ACF | IACF... > <PRINTALL> <TRACE> <XREF>;     
	ABORT <ABEND> <CANCEL> <CANCEL FILE> ...;     
	ARRAY <_ALL_> <_NUMERIC_> <_TEMPORARY_> ...; 

	ATTRIB <FORMAT=format> <INFORMAT=informat> <LABEL='label'> ...; 

	BOUNDS bound1 <, bound2 ...> ; 

	BY variables ;     
	CALL name ( argument-1 [, ...argument-n] );     
	CONTROL variable < value > ... ;     
	DELETE; 

	DEPENDENT variable < initial-values > ... ; 

	DO <OVER> <> <> ...; 

	DROP variable ... ;     
	ELSE <DO> <IF> <THEN> ...; 

	END;     
	ENDOGENOUS variable < initial-values > ... ;     
	ERRORMODEL <CAUCHY( <location, scale> )> <CHISQUARED ( df <, nc> )> <GENERAL(Likelihood <, parm1, parm2,...parmn > )> ...;     
	ESTIMATE <COVB> <OUTCOV> <OUTEST=> ...; 

	EXOGENOUS variable < initial-values > ... ; 

	FIT DROP= ( parameters ...) INITIAL= ( variable = < parameter | constant > ...) PARMS= ( parameters [values] ...) ...;     
	GOTO label;     
	ID variables ;     
	IF <DO> <THEN> <WHEN> ...; 

	INCLUDE model-names ... ;     
	INDEPENDENT variable < initial-values > ... ;     
	INSTRUMENTS EXCLUDE=(parameters) <NOINTERCEPT> _EXOG_ ...; 

	KEEP variable-list ;     
	; 

	LENGTH <DEFAULT=n> ; 

	LINK statement-label;     
	MOMENT variables = moment specification ;     
	NUMBER <INIT expression> ;     
	; 

	OUTVARS variables ;     
	PARAMETERS variable <value> <variable <value>> ... ; 

	PUT <_PAGE_> ;  

	RANGE <RANGE variable < = first > < TO last > > ;     
	RENAME old-name-1=new-name-1 ... <old-name-n=new-name-n>;     
	RESET <DATA=SAS-data-set> <OUTPARMS=SAS-data-set> <PURGE> ...; 

	RESTRICT restriction1 < , restriction2 ...> ;     
	RETAIN variable-list1 value1 < variable-list2 value2 ...> ;     
	RETURN; 

	;     
	SOLVE SATISFY=equation | SATISFY=( equations ) ;     
	STOP ; 

	TEST <LM> <LR> <WALD> ...; 

	VAR variables <initial-values> ... ; 

	WEIGHT variable ;     
	WHEN <DO> ;
