PROC ODSLIST  <CONTENTS =text-string><DATA= SAS-data-set> <NAME =template-name> 
	<PAGEBREAK= NO | YES> <PRINT> <STORE =template-store>;BY  <DESCENDING> variable-1
	<… <DESCENDING> variable-n>
	<NOTSORTED>;CELLSTYLE  expression-1 AS  <style-element-name><[style-attribute-specification(s)] ><, expression-n AS  <style-element-name><[style-attribute-specification(s)]>>;DYNAMIC  variable-name-1 <=value–1<'text-1'>>
	<variable-name-2<=value-2><'text-2' ...>>;MVAR  variable-name-1 <=value–1<'text-1'>>
	<variable-name-2<=value-2><'text-2' ...>>;NMVAR  variable-name-1 <=value–1<'text-1'>>
	<variable-name-2<=value-2><'text-2' ...>>;ITEM  <expression> / <FORMAT =format-name> <STYLE =style-override> <VALUE =integer-value>;
	P  expression / <FORMAT =format-name> <STYLE =style-override>;
	LIST  / <START =integer-value><STYLE =style-override> ;
		ITEM   <expression> / <FORMAT =format-name> <STYLE =style-override> <VALUE =integer-value>;
	END ;END ;TRANSLATE  expression-1 INTO  expression-2 < , expression-n INTO  expression-m;>
