PROC OPTEX <CLASSPARAM> <CODING=NONE | STATIC | ORTH... > <DATA=SAS-data-set> <EPSILON=ε> <NAMELEN=n> <NOCODE> <NOPRINT> <SEED=s> <STATUS=1 | 2 | 3... >;     
	BLOCKS <COVAR=SAS-data-set VAR=( variables )> <DESIGN=SAS-data-set> VAR=( variables ) ...;     
	CLASS <DESCENDING> <ORDER=<DATA | FORMATTED | FREQ>... > <PARAM=<EFFECT | POLYNOMIAL|POLY | REFERENCE|REF>... > ...; 

	EXAMINE <DESIGN> <INFORMATION> <NUMBER=design-number> ...; 

	GENERATE <AUGMENT=SAS-data-set> <CRITERION=<D | A | U>... > <INITDESIGN=<SEQUENTIAL | RANDOM | PARTIAL>... > ...; 

	ID variables ;     
	MODEL <NOINT> <PRIOR=num-list> ; 

	OUTPUT <BLOCKNAME=variable-name> <NUMBER=<DBEST | ABEST | GBEST>... > OUT=SAS-data-set ...;
