PROC OPTMODEL <CDIGITS=number><ERRORLIMIT=<number> | NONE><FD=FORWARD | CENTRAL><FDIGITS=number><FORCEFD=ALL | CON | NONE... ><FORCEPRESOLVE=number | string><INITVAR><INTFUZZ=number><MAXLABLEN=number><MISSCHECK><MSGLIMIT=<number> | NONE><NOINITVAR><NOMISSCHECK><PDIGITS=number><PMATRIX=number><PRESOLVER=NONE | AUTOMATIC | BASIC... ><PRESTOL=number><PRINTLEVEL=0 | 1 | 2><PWIDTH=number><VARFUZZ=number>;     
	CALL name ( argument-1 [, ...argument-n] );
	CLOSEFILE file-specification(s);
	COFOR { index-set } statement;
	CONSTRAINT constraint [, ...constraint];
	CONTINUE ;
	CREATE DATA <FROM [[ key-column(s) ] [ =key-set ] ] column(s)> ;
	DO ; statement(s) ; END;
	DO UNTIL ( logic-expression ) ; statement(s) ; END;
	DO WHILE ( logic-expression ) ; statement(s) ; END;
	DROP identifier-expression;
	ELSE <DO><IF><THEN> ...;
	END;
	ENDSUBMIT;
	EXPAND <FIX><SOLVE><VAR> ...;
	FILE <LRECL=value> ;
	FIX identifier-list [ =( expression ) ];
	FOR { index-set } statement ;
	IF <DO><THEN><WHEN> ...;
	IMPVAR impvar-decl [, ...impvar-decl] ;
	LEAVE ;
	MAX name = expression;
	MIN name = expression;
	NUMBER <INIT expression> ;
	PERFORMANCE <DETAILS><NTHREADS=<<num> | CPUCOUNT>><PARALLELMODE=<DETERMINISTIC|0 | NONDETERMINISTIC|1>> ...;
	PRINT <_PAGE_> ;
	PROBLEM <FROM><INCLUDE> ;
	PROFILE <OFF><ON><PRINT> ...;
	PUT <_PAGE_> ;
	READ DATA <INTO> NOMISS <TRIM> ...;
	RESET OPTIONS <CDIGITS=number><ERRORLIMIT=<<number> | NONE>><FD=<FORWARD | CENTRAL>> ...;
	RESTORE identifier-expression;
	SAVE MPS SAS-data-set  ( OBJECTIVE | OBJ ) name  ;
	SAVE QPS NOOBJECTIVE OBJECTIVE ;
	SET <INIT expression><NUMBER><STRING> ...;
	SOLVE OBJECTIVE RELAXINT WITH ...;
	SOLVE WITH CLP NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH IPNLP NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH LP NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH MILP NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH NETWORK NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH NLP NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH NLPC NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH NLPU NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH QP NOOBJECTIVE OBJECTIVE RELAXINT ...;
	SOLVE WITH SQP NOOBJECTIVE OBJECTIVE RELAXINT ...;
	STOP ;
	STRING <INIT expression> ;
	SUBMIT <OK = identifier-expression><OUT[=] output-argument | (output-arguments)> ;
	UNFIX identifier-list [ =( expression ) ];
	USE PROBLEM identifier-expression ;
	VAR <BINARY><INIT expression><INTEGER> ...;
