PROC OPTNET <DATA_LINKS=SAS-data-set | LINKS=SAS-data-set> <DATA_MATRIX=SAS-data-set | MATRIX=SAS-data-set> <DATA_NODES=SAS-data-set | NODES=SAS-data-set> <DATA_NODES_SUB=SAS-data-set | NODES_SUB=SAS-data-set> <GRAPH_DIRECTION=DIRECTED | UNDIRECTED> <GRAPH_INTERNAL_FORMAT=THIN | FULL> <INCLUDE_SELFLINK> <LOGLEVEL=NONE | BASIC | MODERATE... > <OUT_LINKS=SAS-data-set> <OUT_NODES=SAS-data-set> <STANDARDIZED_LABELS> <TIMETYPE=CPU | REAL>;     
	BICONCOMP <LOGLEVEL=<NONE | BASIC | MODERATE>... > ; 

	CLIQUE <LOGLEVEL=<NONE | BASIC | MODERATE>... > <MAXCLIQUES=number | ALL> <MAXTIME=number> ...; 

	CONCOMP <ALGORITHM=<DFS | UNION_FIND>> <LOGLEVEL=<NONE | BASIC | MODERATE>... > ; 

	CYCLE <LOGLEVEL=<NONE | BASIC | MODERATE>... > <MAXCYCLES=number> <MAXLENGTH=number> ...; 

	DATA_LINKS_VAR <FROM=column> <LOWER=column> <TO=column> ...; 

	DATA_MATRIX_VAR column1 <,column2,...> ; 

	DATA_NODES_VAR <NODE=column> <WEIGHT2=column> <WEIGHT=column> ...; 

	LINEAR_ASSIGNMENT <ID=(column1 <,column2,...>)> <LOGLEVEL=<NONE | BASIC | MODERATE>... > <OUT=SAS-data-set> ...; 

	MINCOSTFLOW <LOGFREQ=number> <LOGLEVEL=<NONE | BASIC | MODERATE>... > <MAXTIME=number> ...; 

	MINCUT <LOGLEVEL=<NONE | BASIC | MODERATE>... > <MAXNUMCUTS=num> <MAXWEIGHT=num> ...; 

	MINSPANTREE <LOGLEVEL=<NONE|0 | BASIC|1 | MODERATE|2>... > <OUT=SAS-data-set> ; 

	PERFORMANCE <DETAILS> <NTHREADS=number | CPUCOUNT> ;     
	SHORTPATH <LOGFREQ=number> <LOGLEVEL=<NONE | BASIC | MODERATE>... > <OUT_PATHS | OUT==SAS-data-set> ...;     
	TRANSITIVE_CLOSURE <LOGLEVEL=<NONE | BASIC | MODERATE>... > <OUT=SAS-data-set> ; 

	TSP <ABSOBJGAP=num> <CONFLICTSEARCH=<AUTOMATIC | NONE | MODERATE>... > <CUTOFF=number> ...;
